package pers.tlbcc.algorithm.basic;

import java.util.ArrayList;
import java.util.List;

/**
 * @author tlbcc
 * @description 计算二进制子串 leetcode 696
 * @date 2020-08-10 15:02
 */
public class CountBinarySubStrings {

    /**
     * 超时
     */
    public static int countBinarySubstrings1(String s) {
        int amount = 0;
        for (int l = 0; l < s.length(); l++) {
            char lv = s.charAt(l);
            // 是否由连续的一个数字变为另一个数字
            boolean change = false;
            int changeIndex = l;
            for (int r = l + 1; r < s.length(); r++) {
                char rv = s.charAt(r);
                if (rv == lv) {
                    // 01连续已结束
                    if (change) {
                        break;
                    }
                    // 连续数字
                    continue;
                }

                // lv与rv不同
                if (!change) {
                    change = true;
                    // 记录变换字符开始位置
                    changeIndex = r;
                }

                int lc = changeIndex - l;
                int rc = r - changeIndex + 1;
                if (lc == rc) {
//                    String subStr = s.substring(l, r + 1);
//                    System.out.println(subStr);
                    amount++;
                    break;
                }
            }
        }
        return amount;
    }

    /**
     * 1609ms
     */
    public static int countBinarySubstrings2(String s) {
        int amount = 0;
        int changeIndex = 0;
        for (int l = 0; l < s.length(); l++) {
            char lv = s.charAt(l);
            // 是否由连续的一个数字变为另一个数字
            boolean change = false;
            for (int r = l + 1; r < s.length(); r++) {
                // changeIndex复用
                if (r <= changeIndex) {
                    r = changeIndex;
                }
                char rv = s.charAt(r);
                if (rv == lv) {
                    // 01连续已结束
                    if (change) {
                        break;
                    }
                    // 连续数字
                    continue;
                }

                // lv与rv不同
                if (!change) {
                    change = true;
                    // 记录变换字符开始位置
                    changeIndex = r;
                }

                int lc = changeIndex - l;
                int rc = r - changeIndex + 1;
                if (lc == rc) {
                    amount += lc;
                    l = changeIndex - 1;
                }

            }
        }
        return amount;
    }

    /**
     * 18ms
     */
    public static int countBinarySubstrings3(String s) {
        int amount = 0;
        List<Integer> list = new ArrayList<>(s.length() / 2);
        list.add(0);
        char v = s.charAt(0);
        for (int i = 1; i < s.length(); i++) {
            if (s.charAt(i) != v) {
                list.add(i);
                v = s.charAt(i);
            }
        }
        list.add(s.length());
        for (int i = 0; i < list.size() - 2; i++) {
            int l = list.get(i);
            int m = list.get(i + 1);
            int r = list.get(i + 2);
            amount += Math.min(m - l, r - m);
        }
        return amount;
    }

    /**
     * 15ms
     */
    public static int countBinarySubstrings(String s) {
        if (s == null || s.length() < 2) {
            return 0;
        }
        int amount = 0;
        int l = 1;
        int r = 0;
        for (int i = 1; i < s.length(); i++) {
            if (s.charAt(i) == s.charAt(i - 1)) {
                l++;
            } else {
                amount += Math.min(l, r);
                r = l;
                l = 1;
            }
        }
        amount += Math.min(l, r);
        return amount;
    }

    public static void main(String[] args) {
        String str = "";
        long st = System.currentTimeMillis();
        int count = countBinarySubstrings(str);
        System.out.println(count);
        long et = System.currentTimeMillis();
        System.out.println(et - st + "ms");
    }
}
